pragma solidity >=0.4.24;

import "../node_modules/openzeppelin-solidity/contracts/math/SafeMath.sol";

contract FlightSuretyData {
    using SafeMath for uint256;

    /********************************************************************************************/
    /*                                       DATA VARIABLES                                     */
    /********************************************************************************************/

    address private contractOwner;                                      // Account used to deploy contract
    bool private operational = true;                                    // Blocks all state changes throughout the contract if false

	struct Airline {
		bool isRegistered;
		bool isFunded;
		uint256 funds;
    }

	struct Flight {        
        address airline;
		string flightId;
		uint256 timestamp;
		bool insureesCredited;
    }

	uint256 nbAirlinesRegistered = 0;
	mapping(address => Airline) airlines;
	mapping(string => uint256) registrationTimestamp; // flightId => timestamp
	mapping(bytes32 => Flight) flights; // flightKey => flight
	mapping(address => mapping(bytes32 => uint256)) passengerPayments; // passengerAddress => flightId => payment
	mapping(bytes32 => address[]) flightInsuranceBuyers; // flightKey => list of insurees
	mapping(address => uint256) passengerCredit; // passengerAddress => insurance payout
	uint256 maxInsurancePurchase = 1; // 1 ether

    /********************************************************************************************/
    /*                                       EVENT DEFINITIONS                                  */
    /********************************************************************************************/


    /**
    * @dev Constructor
    *      The deploying account becomes contractOwner
    */
    constructor
                                (
									address airlineAddress
                                ) 
                                public 
								payable
    {
        contractOwner = msg.sender;
		airlines[airlineAddress] = Airline(true,false,0);
		nbAirlinesRegistered = 1;
    }

    /********************************************************************************************/
    /*                                       FUNCTION MODIFIERS                                 */
    /********************************************************************************************/

    // Modifiers help avoid duplication of code. They are typically used to validate something
    // before a function is allowed to be executed.

    /**
    * @dev Modifier that requires the "operational" boolean variable to be "true"
    *      This is used on all state changing functions to pause the contract in 
    *      the event there is an issue that needs to be fixed
    */
    modifier requireIsOperational() 
    {
        require(operational, "Contract is currently not operational");
        _;  // All modifiers require an "_" which indicates where the function body will be added
    }

    /**
    * @dev Modifier that requires flight insurees have not been credited already
    */
	modifier requireNotAlreadyCredited(bytes32 flightKey) 
    {
        require(flights[flightKey].insureesCredited==false, "Insurees have already been credited");
        _;  // All modifiers require an "_" which indicates where the function body will be added
    }

    /**
    * @dev Modifier that requires the "ContractOwner" account to be the function caller
    */
    modifier requireContractOwner()
    {
        require(msg.sender == contractOwner, "Caller is not contract owner");
        _;
    }

    /********************************************************************************************/
    /*                                       UTILITY FUNCTIONS                                  */
    /********************************************************************************************/

    /**
    * @dev Get operating status of contract
    *
    * @return A bool that is the current operating status
    */      
    function isOperational() 
                            public 
                            view 
                            returns(bool) 
    {
        return operational;
    }


    /**
    * @dev Sets contract operations on/off
    *
    * When operational mode is disabled, all write transactions except for this one will fail
    */    
    function setOperatingStatus
                            (
                                bool mode
                            ) 
                            external
                            requireContractOwner 
    {
        operational = mode;
    }

    /**
    * Test requireIsOperational
    *
    */    
    function testRequireOperational
                            (
                            ) 
                            external
							view
                            requireIsOperational 
    {
    }

	/**
    * Is airline registered
    *
    */    
    function isRegistered
                            (
                                address airlineAddress
                            ) 
                            external
							view
                            requireContractOwner
							returns(bool)
    {
        return airlines[airlineAddress].isRegistered;
    }

	/**
    * Is airline funded
    *
    */    
    function isFunded
                            (
                                address airlineAddress
                            ) 
                            external
							view
                            requireContractOwner
							returns(bool)
    {
        return airlines[airlineAddress].isFunded;
    }

    /**
    * @dev Modify maximum insurance purchase payment
    *
    */    
    function modifyMaximumPurchase
                            (
                                uint256 newMaximum
                            ) 
                            external
                            requireContractOwner 
							requireIsOperational
    {
        maxInsurancePurchase = newMaximum;
    }

    /********************************************************************************************/
    /*                                     SMART CONTRACT FUNCTIONS                             */
    /********************************************************************************************/

   /**
    * @dev Add an airline to the registration queue
    *      Can only be called from FlightSuretyApp contract
    *
    */   
    function registerAirline
                            (   
								address airlineAddress
                            )
                            external
							requireIsOperational
    {
		airlines[airlineAddress] = Airline(true,false,0);
		nbAirlinesRegistered += 1;
    }

   /**
    * @dev Register a flight
    *      Can only be called from FlightSuretyApp contract
    *
    */   
    function registerFlight
                            (
								address airlineAddress,
								string flightId,
								uint256 timestamp
							)
							external
							requireIsOperational
	{
		bytes32 flightKey = getFlightKey(airlineAddress,flightId,timestamp);
		flights[flightKey] = Flight(airlineAddress,flightId,timestamp,false);
		address[] storage InsuranceBuyersLst;
		flightInsuranceBuyers[flightKey] = InsuranceBuyersLst;
		registrationTimestamp[flightId] = timestamp;
	}

   /**
    * @dev Buy insurance for a flight
    *
    */   
    function buyInsurance
                            (           
								string flightId,
								uint256 amount
                            )
                            external
                            payable
							requireIsOperational
    {
		require(msg.sender == tx.origin, "Contracts not allowed");
		require(amount > 0, "Payment must more than 0"); 
		require(amount < maxInsurancePurchase, "Maximum insurance purchase exceeded");
		bytes32 flightKey = getFlightKey(msg.sender,flightId,registrationTimestamp[flightId]);
		require(!(passengerPayments[msg.sender][flightKey]>0),"Passenger has already purchased insurance for this flight");
		flightInsuranceBuyers[flightKey].push(msg.sender);
		passengerPayments[msg.sender][flightKey] = amount;
    }

    /**
     *  @dev Credits payouts to insurees
    */
    function creditInsurees
                                (
									bytes32 flightKey
                                )
                                external
                                requireIsOperational
								requireNotAlreadyCredited(flightKey)
    {	
		address passengerAddress;
		for (uint i = 0; i<flightInsuranceBuyers[flightKey].length; i++) {
			passengerAddress = flightInsuranceBuyers[flightKey][i];
			passengerCredit[passengerAddress] += 3*passengerPayments[passengerAddress][flightKey]/2; // TODO: CHECK that the default value in mapping is 0 for inexistent key
		}
		flights[flightKey].insureesCredited = true;
    }

    /**
     *  @dev Check insuree's eligible payout funds
     *
    */
    function getWithdrawableFunds
                            (
                            )
                            external
							payable
							requireIsOperational
							returns(uint256)
    {
		require(msg.sender == tx.origin, "Contracts not allowed");
		return(passengerCredit[msg.sender]);
    }

    /**
     *  @dev Transfers eligible payout funds to insuree
     *
    */
    function withdrawPayoutFunds
                            (
                            )
                            external
							payable
							requireIsOperational
    {
		require(msg.sender == tx.origin, "Contracts not allowed");
		require(passengerCredit[msg.sender]>0, "No payout funds to withdraw");
		uint256 withdrawableFunds = passengerCredit[msg.sender];
		passengerCredit[msg.sender] = 0;
		msg.sender.transfer(withdrawableFunds); // TODO: verif passager?
    }

   /**
    * @dev Initial funding for the insurance. Unless there are too many delayed flights
    *      resulting in insurance payouts, the contract should be self-sustaining
    *
    */   
    function fund
                            (   
								address airlineAddress,
								uint256 funding
                            )
                            public
                            payable
							requireIsOperational
    {
		airlines[airlineAddress].funds = funding;
		airlines[airlineAddress].isFunded = true;
    }

    function getFlightKey
                        (
                            address airline,
                            string memory flight,
                            uint256 timestamp
                        )
                        pure //view //
                        internal
                        returns(bytes32) 
    {
        return keccak256(abi.encodePacked(airline, flight, timestamp));
    }

    /**
    * @dev Fallback function
    *
    */
    function() 
                            external 
                            payable 
    {
    }


}

